import React, { Component } from "react";
import { Card, CardTitle, CardActions, Grid, Cell } from "react-mdl";

class About extends Component {
  render() {
    return (
      <Grid className="demo-grid-1">
      <Cell col={3}><h2 style={{ marginTop: "0", fontFamily: "Helvetica" }}>About Me</h2>
          <img
            shadow={0}
              src="/images/profilepic.jpeg"
              alt="pic of Mike Carter"
              style={{height: '250px', paddingTop:"10", borderColor: "rgba(255, 255, 255, 0.2)",  }}
               /></Cell>
      <Cell col={4}> <div style={{ textAlign: "center" }}>
        <Card
          shadow={0}
          style={{
            paddingTop: "10",
            width: "600px",
            height: "500px",
            background: "#B4513C"
            ,
          }}
        >
          <CardTitle expand style={{ alignItems: "flex-start", color: "#fff", fontFamily: "Helvetica" }}>


            <h4>
              {" "}
              Hello my name is Michael Carter and I am a full stack developer with a a passion for creating 
              and design. I have experience with server-side development,
              front-end frameworks, and various javascript libraries: React,
              Node.js, jQuery, MySQL, and MongoDB.{" "}
            </h4>
          </CardTitle>
          <CardActions
            border
            style={{
              borderColor: "rgba(255, 255, 255, 0.2)",
              display: "flex",
              boxSizing: "border-box",
              alignItems: "center",
              color: "#fff",
            }}
          ></CardActions>
        </Card>
        </div></Cell>
      <Cell col={4}>4</Cell>


        </Grid>
    );
  }
}

export default About;
