import React, { Component } from "react";
import { Card, CardTitle, CardActions } from "react-mdl";

class About extends Component {
  render() {
    return (
      <div style={{ textAlign: "center" }}>
        <Card
          shadow={0}
          style={{
            paddingTop: "10",
            width: "500px",
            height: "400px",
            background: "#3E4EB8",
          }}
        >
          <CardTitle expand style={{ alignItems: "flex-start", color: "#fff" }}>
            <h2 style={{ marginTop: "0" }}>About Me</h2>
            <div></div><h4>              
              {" "}
              I'm a full stack developer with a a passion for great usability
              and design. I have experience with server-side development,
              front-end frameworks, and various javascript libraries: React,
              Node.js, jQuery, MySQL, and MongoDB.{" "}
            </h4>
          </CardTitle>
          <CardActions
            border
            style={{
              borderColor: "rgba(255, 255, 255, 0.2)",
              display: "flex",
              boxSizing: "border-box",
              alignItems: "center",
              color: "#fff",
            }}
          ></CardActions>
        </Card>
      </div>
    );
  }
}

export default About;
